import React, { useState, useEffect ,useRef} from 'react';
import './App.css';
import io from 'socket.io-client';
import book from './bookStore.jpg';
import logo from './bookLogo.png'

const socket = io('http://localhost:30001');

function App() {
  const [data, setData] = useState([]);
  const [firstname, setFirstname] = useState('');
  const [age, setAge] = useState(0);
  const [mark, setMark] = useState(0);
  const [id, setId] = useState(0);
  const [update, setUpdate] = useState(false);

  useEffect(() => {
    socket.on('initialData', (students) => {
      setData(students);
    });

    socket.on('studentAdded', (student) => {
      setData((prevData) => [...prevData, student]);
    });

    socket.on('studentUpdated', (updatedStudent) => {
      setData((prevData) => prevData.map((student) =>
        student.id === updatedStudent.id ? updatedStudent : student
      ));
    });

    socket.on('studentDeleted', (deletedId) => {
      setData((prevData) => prevData.filter((student) => student.id !== deletedId));
    });

    return () => {
      socket.off('initialData');
      socket.off('studentAdded');
      socket.off('studentUpdated');
      socket.off('studentDeleted');
    };
  }, []);

  const handleEdit = (id) => {
    const dt = data.find(item => item.id === id);
    if (dt) {
      setId(dt.id);
      setUpdate(true);
      setFirstname(dt.firstName);
      setAge(dt.age);
      setMark(dt.marks);
    }
  };

  const handleDelete = (id) => {
    if (id > 0) {
      if (window.confirm("Are you sure that you want to delete this data?")) {
        fetch(`http://localhost:30001/delete-student/${id}`, { // Ensure this matches the backend port
          method: 'DELETE'
        }).then(response => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
        }).catch(error => {
          console.error('There was a problem with the fetch operation:', error);
        });
      }
    }
  };

  const handleSave = () => {
    let error = '';
    if (firstname === '') {
      error += "First Name is required, ";
    }
    if (age <= 0) {
      error += "Please enter the correct age, ";
    }
    if (mark > 100) {
      error += "Please enter correct marks";
    }
    if (error === '') {
      const newStudent = {
        firstName: firstname,
        age: age,
        marks: mark
      };
      fetch('http://localhost:30001/add-student', { // Ensure this matches the backend port
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newStudent)
      }).then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
      }).catch(error => {
        console.error('There was a problem with the fetch operation:', error);
      }).finally(() => {
        handleClear();
      });
    } else {
      alert(error);
      handleClear();
    }
  };

  const handleUpdate = () => {
    const updatedStudent = {
      firstName: firstname,
      age: age,
      marks: mark
    };
    fetch(`http://localhost:30001/update-student/${id}`, { // Ensure this matches the backend port
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(updatedStudent)
    }).then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
    }).catch(error => {
      console.error('There was a problem with the fetch operation:', error);
    }).finally(() => {
      handleClear();
    });
  };

  const handleClear = () => {
    setId(0);
    setFirstname('');
    setAge(0);
    setMark(0);
    setUpdate(false);
  };
  const scrollRef = useRef(null);

  // Function to handle smooth scrolling
  const scrollToSection = () => {
    scrollRef.current.scrollIntoView({ behavior: 'smooth' });
  };
  return (
    <div className="App">
      <div>
      <button onClick={scrollToSection}>Scroll to Section</button>
      <div style={{ height: '1000px' }}>Some content above</div>
      {/* Add a reference to the section you want to scroll to */}
      <div ref={scrollRef}>
        <h2>Section to Scroll To</h2>
        <p>This is the section you want to scroll to.</p>
      </div>
      <div style={{ height: '1000px' }}>Some content below</div>
    </div>
      <div className='nav'>
      <h1>Book Store</h1>
      <img src={logo} alt='book'/>
      <ul>
        <li>Home</li>
        <li>All books</li>
      </ul>
      </div>
      <div className='front'>
      <div style={{display:"flex",flexDirection:"column"}}>
      <span>Book Store For You</span>
      <p>BookStore allows you to add and update existing books in real-time.It ensures immediate updates to book list,providing an interactive user experience to us.</p>
      <button style={{height:"40px",width:"200px",fontSize:"25px",borderRadius:"0px",marginLeft:"40px",marginTop:"10px"}}>All Books</button>
      </div>
      <img src={book} alt='/'/>
      </div>
      <div style={{ display: 'flex', justifyContent: "center", alignContent: "center", marginTop: "230px", marginBottom: "30px", gap: "20px" }}>
        <div>
          <label> FirstName :
            <input type='text' placeholder='Enter your Firstname' onChange={(e) => setFirstname(e.target.value)} value={firstname}></input>
          </label>
        </div>
        <div>
          <label> Age :
            <input type='number' placeholder='Enter your Age' onChange={(e) => setAge(e.target.value)} value={age}></input>
          </label>
        </div>
        <div>
          <label> Marks :
            <input type='number' placeholder='Enter your Marks' onChange={(e) => setMark(e.target.value)} value={mark}></input>
          </label>
        </div>
        <div>
          {
            !update ?
              <button className='btn' onClick={handleSave}>Save</button>
              :
              <button className='btn' onClick={handleUpdate}>Update</button>
          }

        </div>
      </div>
      <table className='table'>
          <tr>
            <th>Sr.No.</th>
            <th>FirstName</th>
            <th>Age</th>
            <th>Marks</th>
            <th>Action</th>
          </tr>
          {
            data.map((item) => {
              return (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.firstName}</td>
                  <td>{item.age}</td>
                  <td>{item.marks}</td>
                  <td>
                    <button onClick={() => handleEdit(item.id)}>Edit</button>
                    <button onClick={() => handleDelete(item.id)}>Delete</button>
                  </td>
                </tr>
              )
            })
          }
      </table>
      <footer>
<div class="footer">
<div class="row">
<ul>
<li>Contact us</li>
<li>Our Services</li>
<li>Privacy Policy</li>
<li>Terms & Conditions</li>
<li>Books</li>
</ul>
</div>

<div class="row">
VINAY PRATAP Â© 2024 - All rights reserved || Designed By: VINAY PRATAP SINGH 
</div>
</div>
</footer>
    </div>
  );
}

export default App;
