import React, { useState, useEffect } from 'react';
import './App.css';
import io from 'socket.io-client';

const socket = io('http://localhost:30001'); // Make sure this matches the port your backend is listening on

function App() {
  const [data, setData] = useState([]);
  const [firstname, setFirstname] = useState('');
  const [age, setAge] = useState(0);
  const [mark, setMark] = useState(0);
  const [id, setId] = useState(0);
  const [update, setUpdate] = useState(false);

  useEffect(() => {
    socket.on('initialData', (students) => {
      setData(students);
    });

    socket.on('studentAdded', (student) => {
      setData((prevData) => [...prevData, student]);
    });

    socket.on('studentUpdated', (updatedStudent) => {
      setData((prevData) => prevData.map((student) =>
        student.id === updatedStudent.id ? updatedStudent : student
      ));
    });

    socket.on('studentDeleted', (deletedId) => {
      setData((prevData) => prevData.filter((student) => student.id !== deletedId));
    });

    return () => {
      socket.off('initialData');
      socket.off('studentAdded');
      socket.off('studentUpdated');
      socket.off('studentDeleted');
    };
  }, []);

  const handleEdit = (id) => {
    const dt = data.find(item => item.id === id);
    if (dt) {
      setId(dt.id);
      setUpdate(true);
      setFirstname(dt.firstName);
      setAge(dt.age);
      setMark(dt.marks);
    }
  };

  const handleDelete = (id) => {
    if (id > 0) {
      if (window.confirm("Are you sure that you want to delete this data?")) {
        fetch(`http://localhost:30001/delete-student/${id}`, { // Ensure this matches the backend port
          method: 'DELETE'
        }).then(response => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
        }).catch(error => {
          console.error('There was a problem with the fetch operation:', error);
        });
      }
    }
  };

  const handleSave = () => {
    let error = '';
    if (firstname === '') {
      error += "First Name is required, ";
    }
    if (age <= 0) {
      error += "Please enter the correct age, ";
    }
    if (mark > 100) {
      error += "Please enter correct marks";
    }
    if (error === '') {
      const newStudent = {
        firstName: firstname,
        age: age,
        marks: mark
      };
      fetch('http://localhost:30001/add-student', { // Ensure this matches the backend port
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newStudent)
      }).then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
      }).catch(error => {
        console.error('There was a problem with the fetch operation:', error);
      }).finally(() => {
        handleClear();
      });
    } else {
      alert(error);
      handleClear();
    }
  };

  const handleUpdate = () => {
    const updatedStudent = {
      firstName: firstname,
      age: age,
      marks: mark
    };
    fetch(`http://localhost:30001/update-student/${id}`, { // Ensure this matches the backend port
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(updatedStudent)
    }).then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
    }).catch(error => {
      console.error('There was a problem with the fetch operation:', error);
    }).finally(() => {
      handleClear();
    });
  };

  const handleClear = () => {
    setId(0);
    setFirstname('');
    setAge(0);
    setMark(0);
    setUpdate(false);
  };

  return (
    <div className="App">
      <h1 style={{ marginTop: "10px" }}>CRED APP</h1>
      <div style={{ display: 'flex', justifyContent: "center", alignContent: "center", marginTop: "30px", marginBottom: "30px", gap: "20px" }}>
        <div>
          <label> FirstName :
            <input type='text' placeholder='Enter your Firstname' onChange={(e) => setFirstname(e.target.value)} value={firstname}></input>
          </label>
        </div>
        <div>
          <label> Age :
            <input type='number' placeholder='Enter your Age' onChange={(e) => setAge(e.target.value)} value={age}></input>
          </label>
        </div>
        <div>
          <label> Marks :
            <input type='number' placeholder='Enter your Marks' onChange={(e) => setMark(e.target.value)} value={mark}></input>
          </label>
        </div>
        <div>
          {
            !update ?
              <button className='btn btn-primary' onClick={handleSave}>Save</button>
              :
              <button className='btn btn-primary' onClick={handleUpdate}>Update</button>
          }
          <button className='btn btn-danger' onClick={handleClear}>Clear</button>
        </div>
      </div>
      <table className='table table-hover'>
        <thead>
          <tr>
            <td>Sr.No.</td>
            <td>FirstName</td>
            <td>Age</td>
            <td>Marks</td>
            <td>Action</td>
          </tr>
        </thead>
        <tbody>
          {
            data.map((item) => {
              return (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.firstName}</td>
                  <td>{item.age}</td>
                  <td>{item.marks}</td>
                  <td>
                    <button onClick={() => handleEdit(item.id)}>Edit</button>
                    <button onClick={() => handleDelete(item.id)}>Delete</button>
                  </td>
                </tr>
              )
            })
          }
        </tbody>
      </table>
    </div>
  );
}

export default App;
